import React, { useState, useEffect, useCallback } from 'react';

function Day(props) {
  const todayNum = new Date().getDate();

  return (
    <div className="calendar-widget">
      {props.isCurrentMonth ? (
        props.isToday ? ( 
        <h6 className="today">{props.date}</h6> 
      ) : ( <h6>{props.date}</h6> )
      ) : (
        <h6 id="not-current-month">{props.date}</h6>
      )}
    </div>
  );
}

function Calendar({ toggleSidebar }) {
  const today = new Date();
  const [days, setDays] = useState([]);
  const [month, setMonth] = useState(today.getMonth());
  const [year, setYear] = useState(today.getFullYear());
  const dayOfWeek = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];
  const monthString = [
    "January", "February", "March", "April", "May", "June",
    "July", "August", "September", "October", "November", "December"
  ];

  const getFirstDayOfMonth = (month, year) => {
    const firstDay = new Date(year, month, 1);
    return firstDay.getDay();
  };

  const getMonthDays = (month, year) => {
    if (month === 3 || month === 5 || month === 8 || month === 10) {
      return 30;
    } else if (month === 1) {
      return (year % 4 === 0 && year % 100 !== 0) || year % 400 === 0 ? 29 : 28;
    } else {
      return 31;
    }
  };

  const display = useCallback(() => {
    const newDays = [];
    const prevMonthDays = getMonthDays(month - 1 < 0 ? 11 : month - 1, year);
    const firstDayOfMonth = getFirstDayOfMonth(month, year);

    // Days from previous month
    for (let i = 0; i < firstDayOfMonth; i++) {
      newDays.push({ date: prevMonthDays - firstDayOfMonth + 1 + i, isCurrentMonth: false, isToday: false });
    }

    // Days of the current month
    for (let i = 1; i <= getMonthDays(month, year); i++) {
      newDays.push({ 
        date: i, 
        isCurrentMonth: true,
        isToday: i === today.getDate() && month === today.getMonth() && year === today.getFullYear()
      });
    }

    // Fill remaining slots with next month's days
    let count = 1;
    while (newDays.length % 7 !== 0) {
      newDays.push({ date: count++, isCurrentMonth: false, isToday: false});
    }

    setDays(newDays);
  }, [month, year]);

  useEffect(() => {
    display();
  }, [month, year, display]);

  const updateMonth = (direction) => {
    if (direction === -1) {
      setMonth((prevMonth) => (prevMonth - 1 < 0 ? 11 : prevMonth - 1));
      setYear((prevYear) => (month - 1 < 0 ? prevYear - 1 : prevYear));
    } else {
      setMonth((prevMonth) => (prevMonth + 1 > 11 ? 0 : prevMonth + 1));
      setYear((prevYear) => (month + 1 > 11 ? prevYear + 1 : prevYear));
    }
  };

  return (
    <div>
        <head>
            <title>Calendar</title>
        </head>
        <section className="Bar">
            <button id="menu-toggle" className="menu-toggle" onClick={toggleSidebar}>â˜°</button>
            <button className="calendar-arrow" onClick={() => updateMonth(-1)}>&lt;</button>
            <h1>{monthString[month]} {year}</h1>
            <button className="calendar-arrow" onClick={() => updateMonth(1)}>&gt;</button>
        </section>
        <article className="calendar-grid">
        {dayOfWeek.map((dayName, index) => (
          <h6 key={index} className="day-of-week">{dayName}</h6>
        ))}
        {days.map((day, index) => (
          <Day key={index} date={day.date} isCurrentMonth={day.isCurrentMonth} isToday={day.isToday} />
        ))}
      </article>
    </div>
  );
}

export default Calendar;
