import React, { useState } from 'react';
import { Calendar, Views, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import 'react-big-calendar/lib/addons/dragAndDrop/styles.css';
import withDragAndDrop from 'react-big-calendar/lib/addons/dragAndDrop';

const localizer = momentLocalizer(moment);
const DnDCalendar = withDragAndDrop(Calendar);

export default function CalendarContainer() {
  const [events, setEvents] = useState([
    {
      id: 1,
      title: 'Existing Event',
      start: new Date(),
      end: new Date(new Date().setHours(new Date().getHours() + 1)),
    },
  ]);

  const onEventDrop = ({ event, start, end }) => {
    const updatedEvents = events.map((evt) =>
      evt.id === event.id ? { ...evt, start, end } : evt
    );
    setEvents(updatedEvents);
  };

  const onDropFromSidebar = (start, end, draggedEvent) => {
    const newEvent = {
      id: new Date().getTime(),
      title: draggedEvent.title,
      start,
      end,
    };
    setEvents([...events, newEvent]);
  };

  return (
    <div style={{ height: '80vh', margin: '0 auto' }}>
      <DnDCalendar
        className="my-custom-calendar" // <--- Add your custom class
        localizer={localizer}
        events={events}
        defaultView={Views.MONTH}
        views={[Views.MONTH, Views.WEEK]}
        selectable
        onEventDrop={onEventDrop}
        onDropFromOutside={({ start, end, draggedEvent }) =>
          onDropFromSidebar(start, end, draggedEvent)
        }
        dragFromOutsideItem={(item) => item}
        startAccessor="start"
        endAccessor="end"
        style={{ minHeight: '600px' }}
      />
    </div>
  );
}
