import React, { useState, useEffect, useCallback } from "react";
import { DndProvider } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";

function Day({ date, isCurrentMonth, isToday }) {
  return (
    <div className={`calendar-grid ${isToday ? "today" : ""} ${isCurrentMonth ? "" : "not-current-month"}`}>
      <h6>{date}</h6>
    </div>
  );
}

function Week({ days }) {
  return (
    <div className="week-view">
      {days.map((day, index) => (
        <Day key={index} date={day.date} isCurrentMonth={day.isCurrentMonth} isToday={day.isToday} />
      ))}
    </div>
  );
}

function Calendar() {
  const today = new Date();
  const [month, setMonth] = useState(today.getMonth());
  const [year, setYear] = useState(today.getFullYear());
  const [currentWeek, setCurrentWeek] = useState(0);
  const [days, setDays] = useState([]);
  const [view, setView] = useState("month"); // 'month' or 'week'

  const dayOfWeek = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];
  const monthString = [
    "January", "February", "March", "April", "May", "June",
    "July", "August", "September", "October", "November", "December"
  ];

  const getDaysInMonth = (month, year) => {
    return new Date(year, month + 1, 0).getDate();
  };

  const generateDays = useCallback(() => {
    const firstDayOfMonth = new Date(year, month, 1).getDay();
    const daysInCurrentMonth = getDaysInMonth(month, year);
    const daysInPreviousMonth = getDaysInMonth(month - 1 < 0 ? 11 : month - 1, year);

    const generatedDays = [];
    // Days from previous month
    for (let i = firstDayOfMonth - 1; i >= 0; i--) {
      generatedDays.push({
        date: daysInPreviousMonth - i,
        isCurrentMonth: false,
        isToday: false,
      });
    }

    // Days from current month
    for (let i = 1; i <= daysInCurrentMonth; i++) {
      generatedDays.push({
        date: i,
        isCurrentMonth: true,
        isToday: i === today.getDate() && month === today.getMonth() && year === today.getFullYear(),
      });
    }

    // Fill up remaining days to complete the final week
    while (generatedDays.length % 7 !== 0) {
      generatedDays.push({
        date: generatedDays.length - daysInCurrentMonth + 1,
        isCurrentMonth: false,
        isToday: false,
      });
    }

    setDays(generatedDays);
  }, [month, year]);

  const getCurrentWeek = () => {
    const startIndex = currentWeek * 7;
    return days.slice(startIndex, startIndex + 7);
  };

  const changeWeek = (direction) => {
    setCurrentWeek((prev) => {
      const newWeek = prev + direction;
      const maxWeeks = Math.ceil(days.length / 7) - 1;

      if (newWeek < 0) {
        setMonth((prevMonth) => (prevMonth - 1 + 12) % 12);
        setYear((prevYear) => (month === 0 ? prevYear - 1 : prevYear));
        return maxWeeks; // Last week of the previous month
      } else if (newWeek > maxWeeks) {
        setMonth((prevMonth) => (prevMonth + 1) % 12);
        setYear((prevYear) => (month === 11 ? prevYear + 1 : prevYear));
        return 0; // First week of the next month
      }

      return newWeek;
    });
  };

  const changeMonth = (direction) => {
    setMonth((prevMonth) => (prevMonth + direction + 12) % 12);
    setYear((prevYear) => (month + direction < 0 ? prevYear - 1 : month + direction > 11 ? prevYear + 1 : prevYear));
    setCurrentWeek(0); // Reset to the first week when changing the month
  };

  useEffect(() => {
    generateDays();
  }, [month, year, generateDays]);

  return (
    <DndProvider backend={HTML5Backend}>
      <div>
        <section className="Bar">
          <button className="calendar-arrow" onClick={() => (view === "month" ? changeMonth(-1) : changeWeek(-1))}>
            &lt;
          </button>
          <h1>
            {monthString[month]} {year}
          </h1>
          <button className="calendar-arrow" onClick={() => (view === "month" ? changeMonth(1) : changeWeek(1))}>
            &gt;
          </button>
          <button className="view-toggle" onClick={() => setView((prevView) => (prevView === "month" ? "week" : "month"))}>
            {view === "month" ? "Week View" : "Month View"}
          </button>
        </section>
        <article className="calendar-grid">
          {dayOfWeek.map((dayName, index) => (
            <h6 key={index} className="calendar-grid">{dayName}</h6>
          ))}
          {view === "month" ? (
            <div className="month-view">
              {days.map((day, index) => (
                <Day key={index} date={day.date} isCurrentMonth={day.isCurrentMonth} isToday={day.isToday} />
              ))}
            </div>
          ) : (
            <Week days={getCurrentWeek()} />
          )}
        </article>
      </div>
    </DndProvider>
  );
}

export default Calendar;
