import React, { useState, useEffect, useCallback } from 'react';
import { DndProvider, useDrag, useDrop } from 'react-dnd';
import { HTML5Backend } from 'react-dnd-html5-backend';

function Day({ date, isCurrentMonth, isToday, onDropWidget }) {
  const [, drop] = useDrop(() => ({
    accept: 'WIDGET',
    drop: (item) => onDropWidget(item.id, date),
  }));

  return (
    <div ref={drop} className={`calendar-widget ${isToday ? 'today' : ''} ${isCurrentMonth ? '' : 'not-current-month'}`}>
      <h6>{date}</h6>
    </div>
  );
}

function Week({ weekDays, onDropWidget }) {
  return (
    <div className="week-view">
      {weekDays.map((day, index) => (
        <Day
          key={index}
          date={day.date}
          isCurrentMonth={day.isCurrentMonth}
          isToday={day.isToday}
          onDropWidget={onDropWidget}
        />
      ))}
    </div>
  );
}

function Sidebar({ widgets, moveWidget }) {
  const [, drop] = useDrop(() => ({
    accept: 'WIDGET',
    drop: (item) => moveWidget(item.id),
  }));

  return (
    <div ref={drop} className="sidebar">
      {widgets.map((widget) => (
        <Widget key={widget.id} widget={widget} />
      ))}
    </div>
  );
}

function Widget({ widget }) {
  const [, drag] = useDrag(() => ({
    type: 'WIDGET',
    item: { id: widget.id },
  }));

  return (
    <div ref={drag} className="widget">
      {widget.name}
    </div>
  );
}

function Calendar({ toggleSidebar }) {
  const today = new Date();
  const [days, setDays] = useState([]);
  const [month, setMonth] = useState(today.getMonth());
  const [year, setYear] = useState(today.getFullYear());
  const [view, setView] = useState('month');
  const [currentWeek, setCurrentWeek] = useState(0);
  const [widgets, setWidgets] = useState([
    { id: 1, name: 'Job 1' },
    { id: 2, name: 'Job 2' },
  ]);

  const dayOfWeek = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];
  const monthString = [
    "January", "February", "March", "April", "May", "June",
    "July", "August", "September", "October", "November", "December"
  ];

  const getFirstDayOfMonth = (month, year) => new Date(year, month, 1).getDay();
  const getMonthDays = (month, year) => [31, year % 4 === 0 && year % 100 !== 0 || year % 400 === 0 ? 29 : 28, 31, 30, 31, 30, 31, 31, 30, 31, 30, 31][month];

  const display = useCallback(() => {
    const newDays = [];
    const prevMonthDays = getMonthDays(month - 1 < 0 ? 11 : month - 1, year);
    const firstDayOfMonth = getFirstDayOfMonth(month, year);

    // Add previous month's days
    for (let i = 0; i < firstDayOfMonth; i++) {
      newDays.push({ date: prevMonthDays - firstDayOfMonth + 1 + i, isCurrentMonth: false, isToday: false });
    }

    // Add current month's days
    for (let i = 1; i <= getMonthDays(month, year); i++) {
      newDays.push({ 
        date: i, 
        isCurrentMonth: true,
        isToday: i === today.getDate() && month === today.getMonth() && year === today.getFullYear()
      });
    }

    // Add next month's days
    let count = 1;
    while (newDays.length % 7 !== 0) {
      newDays.push({ date: count++, isCurrentMonth: false, isToday: false });
    }

    setDays(newDays);
  }, [month, year]);

  useEffect(() => {
    display();
  }, [month, year, display]);

  const getWeekDays = () => {
    const start = currentWeek * 7;
    return days.slice(start, start + 7);
  };

  const updateMonth = (direction) => {
    setMonth((prevMonth) => (prevMonth + direction + 12) % 12);
    setYear((prevYear) => (month + direction < 0 ? prevYear - 1 : month + direction > 11 ? prevYear + 1 : prevYear));
    setCurrentWeek(0);
  };

  const moveWidget = (id) => {
    setWidgets((prevWidgets) => prevWidgets.filter((widget) => widget.id !== id));
  };

  const onDropWidget = (id, date) => {
    console.log(`Widget ${id} dropped on date: ${date}`);
  };

  const updateWeek = (direction) => {
    setCurrentWeek((prevWeek) => Math.max(0, Math.min(prevWeek + direction, Math.floor(days.length / 7) - 1)));
  };

  return (
    <DndProvider backend={HTML5Backend}>
      <div>
        <section className="Bar">
          <button id="menu-toggle" className="menu-toggle" onClick={toggleSidebar}>â˜°</button>
          <button className="calendar-arrow" onClick={() => updateMonth(-1)}>&lt;</button>
          <h1>{monthString[month]} {year}</h1>
          <button className="calendar-arrow" onClick={() => updateMonth(1)}>&gt;</button>
          <button className="view-toggle" onClick={() => setView((prevView) => (prevView === 'month' ? 'week' : 'month'))}>
            {view === 'month' ? 'Week View' : 'Month View'}
          </button>
        </section>
        <article className="calendar-grid">
          {view === 'month' ? (
            <>
              {dayOfWeek.map((dayName, index) => (
                <h6 key={index} className="day-of-week">{dayName}</h6>
              ))}
              {days.map((day, index) => (
                <Day key={index} date={day.date} isCurrentMonth={day.isCurrentMonth} isToday={day.isToday} onDropWidget={onDropWidget} />
              ))}
            </>
          ) : (
            <div>
              <button className="week-arrow" onClick={() => updateWeek(-1)}>&lt;</button>
              <Week weekDays={getWeekDays()} onDropWidget={onDropWidget} />
              <button className="week-arrow" onClick={() => updateWeek(1)}>&gt;</button>
            </div>
          )}
        </article>
        <Sidebar widgets={widgets} moveWidget={moveWidget} />
      </div>
    </DndProvider>
  );
}

export default Calendar;
